import React, { useState, useEffect } from "react";
import loadData from "../../api/CallApi";
import { Button, Checkbox, Tooltip, Image } from "antd";
import { EditOutlined, DeleteTwoTone } from "@ant-design/icons";

const ProductsList = (data) => {
  const [products, setProducts] = useState();
  useEffect(() => {
    loadData(data, setProducts);
  }, [data]);
  const CheckboxGroup = Checkbox.Group;
  const [checkedList, setCheckedList] = useState([]);
  const checkAll = products?.length === checkedList?.length;
  const indeterminate =
    checkedList?.length > 0 && checkedList?.length < products?.length;
  const onChange = (list) => {
    setCheckedList(list);
  };
  const onCheckAllChange = (e) => {
    setCheckedList(
      e.target.checked ? products?.map((product) => product.id) : []
    );
  };

  const checkboxOptions = products?.map((product, index) => ({
    label: (
      <>
        <div
          key={index}
          className="flex items-center justify-between border-s text-center"
        >
          <Tooltip title="Mã sản phẩm">
            <div className=" py-3 w-[10%]">{product.id}</div>
          </Tooltip>
          <div className=" py-3 w-[15%]">
            <button>
              <Image
                src={product.img}
                className="w-full h-full object-cover"
                fallback="data:image/png;base64,"
              />
            </button>
          </div>
          <div className=" py-3 w-[29%]">{product.name}</div>
          <Tooltip title="Giá bán">
            <div className=" py-3 w-[10%]">{product.price}</div>
          </Tooltip>
          <Tooltip title="Giảm giá">
            <div className=" py-3 w-[10%]">{product.sale}</div>
          </Tooltip>
          <Tooltip title="Giá sau giảm">
            <div className=" py-3 w-[10%]">{product.price}</div>
          </Tooltip>
          <Tooltip title="Tồn kho">
            <div className=" py-3 w-[8%]">{product?.quantity}</div>
          </Tooltip>
          <div className="flex items-center justify-evenly w-[8%]">
            <Tooltip title="Chỉnh sửa">
              <button>
                <EditOutlined className="p-2 rounded-lg hover:bg-gray-200" />
              </button>
            </Tooltip>
            <Tooltip title="Xoá">
              <button>
                <DeleteTwoTone
                  className="p-2 rounded-lg hover:bg-red-200"
                  twoToneColor="#ff0000"
                />
              </button>
            </Tooltip>
          </div>
        </div>
      </>
    ),
    value: product.id,
  }));

  return (
    <>
      <div className="flex items-center gap-2 border border-b-0 text-center mt-3">
        <div className="py-3 ">
          <Checkbox
            className="border-0"
            indeterminate={indeterminate}
            onChange={onCheckAllChange}
            checked={checkAll}
          ></Checkbox>
        </div>
        <div className="flex-1 flex items-center justify-between h-full border-s">
          <div className="w-[10%] border-e">Mã sản phẩm</div>
          <div className="w-[15%] border-e">Ảnh</div>
          <div className="w-[29%] border-e">Tên sản phẩm</div>
          <div className="w-[10%] border-e">Giá bán</div>
          <div className="w-[10%] border-e">Giảm giá</div>
          <div className="w-[10%] border-e">Giá sau giảm</div>
          <div className="w-[8%] border-e">Tồn kho</div>
          <div className="w-[8%] border-s">Chức năng</div>
        </div>
      </div>
      <CheckboxGroup
        options={checkboxOptions}
        value={checkedList}
        onChange={onChange}
      />
    </>
  );
};

export default ProductsList;
